import React from 'react';
import zxcvbn from "zxcvbn"

import "./passwordStrenghtMeter_style.css"

function PasswordStrenghtMeter(props) {

    const testPassword = zxcvbn(props.password);
    const passwordStrenght = (testPassword.score * 100/3).toFixed(0);
    
    const changePasswordColor = () => ({
        width: `${passwordStrenght}%`,
        background: progressColor(),
        height: '100%',
        borderRadius: '30px',
        transition: 'all 0.3s'
    })

    const showPassStrenght = () => {
        if (passwordStrenght == 0) {
            props.setPasswordAccepted(false)
            return 'Muito fraca'
        }
        if (passwordStrenght == 33) {
            props.setPasswordAccepted(false)
            return 'Fraca'
        }
        if (passwordStrenght == 67) {
            props.setPasswordAccepted(false)
            return 'Boa'
        }
        if (passwordStrenght >= 100) {
            props.setPasswordAccepted(true)
            return 'Forte'
        }
    }

    const progressColor = () => {
        if (passwordStrenght == 0) return 'rgb(133, 133, 133)'
        if (passwordStrenght == 33) return 'rgb(243, 7, 7)'
        if (passwordStrenght == 67) return 'rgb(255, 136, 0)'
        if (passwordStrenght >= 100) return 'rgb(11, 212, 11)'
    }

    return (
        <>
            <div className="passwordStrenghtMeter">
                <div className="progress-bar" style={changePasswordColor()}></div>
            </div>
            <p 
                className='passwordStrenght-label'
                style={{color: progressColor()}}
            >{showPassStrenght()}</p>
        </>
    );
}

export default PasswordStrenghtMeter;
