import { React, useState } from 'react';

import "./containerViewCard_style.css"

function ContainerViewCard(props) {

    const [taskName, setTaskName] = useState(props.taskName);
    const [taskDescription, setTaskDescription] = useState(props.taskDescription);
    const [taskPriority, setTaskPriority] = useState(props.taskPriority);

    const token = sessionStorage.getItem("token")
    const userID = sessionStorage.getItem("userID");
    
    if (token === undefined || token === null) {
        window.location.href = "/";
    } 

    const handleEditTask = (e) => {
        e.preventDefault();

        const formdata = new FormData();
        formdata.append("userTask", [taskName, taskDescription, Number(taskPriority), userID]);
        
        // Enviando requisição para o back-end com token de acesso
        // fetch("http://localhost:5000/createTask", {
        //     method: 'POST',
        //     body: formdata,
        //     headers: {
        //         Authorization: "Bearer " + token
        //     }
        // })
        // .then(response => response.json())
        // .then(data => {
            
        //     alert(data.message);        
            
        //     if (data.status) {
        //         props.setTrigger(false);
        //     }
        // })
        // .catch(err => console.error(err))
            
        setTaskName("");
        setTaskDescription("");
        setTaskPriority("");
    }

    return (props.trigger) ? (
        <div className='pop-up'>
            <div className='popUp-content'>
                
                <button className='btn-close' title='Fechar painel' onClick={() => props.setTrigger(false)}>X</button>
                    
                <form className='taskForm' onSubmit={handleEditTask}>
                    
                    <input 
                        required
                        type="text" 
                        value={props.taskName} 
                        onChange={(e) => setTaskName(e.target.value)} 
                        placeholder='Nome da tarefa'
                    />
                    
                    <textarea 
                        name="description" 
                        id="description-textarea" 
                        value={props.taskDescription}
                        onChange={(e) => setTaskDescription(e.target.value)}
                        cols="10" 
                        rows="10" 
                        placeholder='Descrição'
                    ></textarea>
                    
                    <select 
                        required
                        name="priorityDropdown" 
                        id="priorityDropdown"
                        value={props.taskPriority}
                        onChange={(e) => setTaskPriority(e.target.value)}
                    >
                        <option value="">Selecione a prioridade</option>
                        <option value="1">Prioridade 1</option>
                        <option value="2">Prioridade 2</option>
                        <option value="3">Prioridade 3</option>
                        <option value="4">Prioridade 4</option>
                    </select>

                    <button type='submit' title='Criar tarefa' className='btn-create'>Criar</button> 
                    
                </form>
                
            </div>
        </div>
    ) : "";
}

export default ContainerViewCard;
