import React, { useState } from "react"

import './login_style.css';
import "../../styles/global.css"
import eye_icon from "../../img/eye-icon.png"

import PasswordInput from "../../components/PasswordInput/index_passwordInput";

function Login() {

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('user_credentials', [username, password]);

    fetch('http://localhost:5000/login', {
      method: 'POST',
      body: formData
    })
    .then(response => response.json())
    .then(data => {
      console.log('Resposta do servidor:', data)
    })
    .catch(err => console.error(err));

    setUsername("")
    setPassword("")
  }

  const handleTogglePassword = () => {
    setIsPasswordVisible(!isPasswordVisible);
  };

    return (
      <body>
        <div className="login-container">
          <h1 className='singIn-title'>Login</h1>

          <form className="singIn-form" onSubmit={handleSubmit}>
            <div className="singIn-inputs">
              <label className='singIn-labels' htmlfor="username">Email:</label>
              <input 
                  type="email"
                  id="userEmail"
                  className="username_input"
                  onChange={(e) => setUsername(e.target.value)}
                  value={username}
                  required
              />
              <label className='singIn-labels' htmlfor="userEmail">Senha:</label>

              {/* <div className='password-container'>
                <input 
                    type={isPasswordVisible ? 'text' : 'password'} 
                    id="userPassword"
                    className="password_input"
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                    required
                    />
                <img 
                  className='eye-icon' 
                  src={ eye_icon } 
                  alt='eye-icon'
                  onClick={handleTogglePassword}
                  />
              </div>
            */}

              <PasswordInput 
                state={password}
                onChange={setPassword}
              />

            </div>

            <div className='container-btnSingIn'> 
              <button 
                type='submit'
                className='btn_singIn'
                >Entrar
              </button>
            </div>
          </form>

          <div className='link-singUp'>
            <p>NÃ£o tem cadastro?</p>
            <a href='/singUp'>Clique aqui</a>
          </div>

        </div>    
      </body>
  );
}

export default Login;
