import React, { useState } from "react";
import eye_icon from "../../../img/eye-icon.png";
import "./passwordInput_style.css";

function PasswordInput({ id, state, onChange, showRegexError }) {
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const validatePassword = (password) => {
    
    // Expressão regular para garantir que a senha seja forte
    const strongPasswordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*])(?=.{8,})/;

    if (password) {
        if (strongPasswordRegex.test(password))
            return false
        else
            return true
    }
  };

  const handleTogglePassword = () => {
    setIsPasswordVisible(!isPasswordVisible);
  };

  const passwordError = validatePassword(state);

  return (
    <div className="inputPassword-bodyContainer">

        <div className="inputPassword-container">

        <input
            type={isPasswordVisible ? "text" : "password"}
            id={id}
            className="passwordInput"
            onChange={(e) => onChange(e.target.value)}
            value={state}
            required
            />
        <img
            className="eye-icon"
            src={eye_icon}
            alt="eye-icon"
            onClick={handleTogglePassword}
            />
        </div>

        { passwordError && showRegexError ? (
        
            <div className="password-error">
                <strong style={{textAlign: "center"}}>Senha fraca</strong>
                <p>Deve conter pelo menos 8 caracteres:</p>
                <ul>
                    <li>Incluindo letras maiúsculas, minúsculas</li>
                    <li>Números</li>
                    <li>Caracteres especiais</li>
                </ul>
            </div>
            
            ) : "" }
    
    </div>
  );
}

export default PasswordInput;
