import React, { useState } from 'react';

import "./home_style.css"
import "../../../styles/global.css"

import Navbar from "../../components/Navbar/index_navbar";
import ContainerCreateCard from "../../components/ContainerCreateCard/containerCreateCard_index";
// import Board from "../../components/Board/index_board";

function Home() {

    const token = sessionStorage.getItem("token")
    
    if (token === undefined || token === null) {
        window.location.href = "/";
    }    

    const handleCreateTask = () => {

        setPopupVisible(true);
        // const token = sessionStorage.getItem("token");

        // const opts = {
        //     headers: {
        //         Authorization: "Bearer " + token
        //     }
        // }

        // // Enviando requisição para o back-end com token de acesso
        // fetch("http://localhost:5000/createTask", opts)
            
        //     .then(response => response.json())
        //     .then(data => {

        //         alert(data.message)
        //     })
    }

    const [isPopupVisible, setPopupVisible] = useState(false);

    return (


        <div className="body-container">
            
            <Navbar />
            
            <ContainerCreateCard trigger={isPopupVisible}>
                <h3>Teste Pop-up</h3>
            </ContainerCreateCard>


            <div className="home-container">
                {/* <button onClick={openPopup}>Abrir Pop-up</button>
                    {isPopupVisible && (
                        <ContainerCreateCard onClose={closePopup}>
                        <h2>Conteúdo do Pop-up</h2>
                        <p>Seu conteúdo aqui.</p>
                        </ContainerCreateCard>
                        */}
            

                <button className="btn-createTask" title="Criar nova tarefa" onClick={handleCreateTask}>+</button>

                <div className="boards">

                    {/* <Board 
                        header={"TO DO"}
                    />
                    
                    <Board 
                        header={"DOING"}
                    />
                    
                    <Board 
                        header={"DONE"}
                    />
                    */}

                    <aside>
                        <h3>TO DO</h3>
                        <section>
                            
                            <div className="task-card">
                                <h4>Dar banho no peixe</h4>
                                <small>Usar toalha do banheiro</small>    
                            </div>
                            
                            <div className="task-card">
                                <h4>Tarefa facul</h4>
                                <small>Seguir anotações do caderno</small>    
                            </div>
                            
                            <div className="task-card">
                                <h4>Limpar quarto</h4>
                                <small></small>    
                            </div>
                        
                        </section>
                    </aside>

                    <aside>
                        <h3>DOING</h3>
                        <section>
                        </section>
                    </aside>

                    <aside>
                        <h3>DONE</h3>
                        <section>
                        </section>
                    </aside>
                    
                </div>
            </div>
        
        </div>
    )
}

export default Home;
