import './login_style.css';
import "../../styles/global.css"
import eye_icon from "../../img/eye-icon.png"

import React, { useState } from "react"

function Login() {

  // const [data, setData] = useState([{}])

  // useEffect(() => {
  //   fetch("/teste")
  //   .then(response => response.json())
  //   .then(data => {
  //     setData(data)
  //   })
  // }, [])
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (username !== "" && password !== "") {

      const formData = new FormData();
      formData.append('user_credentials', [username, password]);

      fetch('http://localhost:5000/login', {
        method: 'POST',
        body: formData
      })
      .then(response => response.json())
      .then(data => {
        console.log('Resposta do servidor:', data)
      })
      .catch(err => console.error(err));

    } else {
      alert("Favor inserir usuário e senha para prosseguir")
    }

    setUsername("")
    setPassword("")
  }

  const handleTogglePassword = () => {
    setIsPasswordVisible(!isPasswordVisible);
  };

    return (
      <body>
        <div className="login-container">
          <h1 className='singIn-title'>Login</h1>

          {/* <div className='userCredentials-inputs'>

            <input 
              type='text' 
              onChange={ (e) => setUsername(e.target.value) } 
              id='username' 
              className='username_input' 
              placeholder='Insira seu usuário'
              value={username}
            />

            <div className='password-container'>
              <input 
                type={isPasswordVisible ? 'text' : 'password'} 
                onChange={ (e) => setPassword(e.target.value) } 
                id='password' 
                className='password_input' 
                placeholder='Insira sua senha'
                value={password}
              />
              <img 
                className='eye-icon' 
                src={ eye_icon } 
                alt='eye-icon'
                onClick={handleTogglePassword}
              />

            </div>

            <button 
              type='button' 
              onClick={ handleSubmit } 
              className='btn_singIn'
            >Entrar</button>

          </div>
           */}

          <form className="singIn-form" onSubmit={handleSubmit}>
            <div className="singIn-inputs">
              <label className='singIn-labels' htmlfor="username">Email:</label>
              <input 
                  type="email"
                  id="userEmail"
                  className="userEmail"
                  onChange={(e) => setUsername(e.target.value)}
                  value={username}
                  required
              />
              <label className='singIn-labels' htmlfor="userEmail">Senha:</label>

              <div className='password-container'>
                <input 
                    type={isPasswordVisible ? 'text' : 'password'} 
                    id="userPassword"
                    className="userPassword"
                    onChange={(e) => setPassword(e.target.value)}
                    value={password}
                    required
                    />
                <img 
                  className='eye-icon' 
                  src={ eye_icon } 
                  alt='eye-icon'
                  onClick={handleTogglePassword}
                  />
              </div>
            </div>
          </form>

          <div className='links'>
            <p>Não tem cadastro?</p>
            <a href='/singUp'>Clique aqui</a>
          </div>

        </div>    
      </body>
  );
}

export default Login;
