import React, { useState } from "react";

import "./passwordInput_style.css";

import eye_icon from "../../../assets/svg/eye-icon.svg";
import hiddenEye_icon from "../../../assets/svg/hiddenEye-icon.svg";

function PasswordInput({ id, state, onChange, showRegexError, setResultRegex }) {
  
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const validatePassword = (password) => {
    
    // Expressão regular para garantir que a senha seja forte
    const strongPasswordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*])(?=.{8,})/;

    if (password && showRegexError) {
        if (strongPasswordRegex.test(password)) {
            setResultRegex(true)
            return true
        
        } else {
            setResultRegex(false)
            return false
        }
    } 
    return true
  };

  const handleTogglePassword = () => {
    setIsPasswordVisible(!isPasswordVisible);
  };

  const passwordCorrect = validatePassword(state);

  const changePasswordColor = () => ({
    width: '70%',
    background: 'red',
    height: '7px',
    borderRadius: '30px'
  })

  return (
    <div className="inputPassword-bodyContainer">

        <div className="inputPassword-container">

        <input
            type={isPasswordVisible ? "text" : "password"}
            id={id}
            className="passwordInput"
            onChange={(e) => onChange(e.target.value)}
            value={state}
            required
            />
        <img
            className="eye-icon"
            src={isPasswordVisible ? hiddenEye_icon : eye_icon}
            alt="eye-icon"
            onClick={handleTogglePassword}
            />
        </div>

        { showRegexError ? (
          <div className="passwordStrenghtMeter">
            <div className="progress-bar" style={changePasswordColor()}></div>
          </div>
        ) : "" }

        {/* { !passwordCorrect && showRegexError ? (
        
            <div className="password-error">
                <strong>Senha fraca!</strong>
                <ul>
                  <li>Deve conter pelo menos 8 caracteres</li>
                  <li>Incluindo letras maiúsculas e minúsculas</li>
                  <li>Números</li>
                  <li>Caracteres especiais</li>
                </ul>
            </div>
            
            ) : "" } */}
    
    </div>
  );
}

export default PasswordInput;
