import { React, useState } from 'react'

import { Droppable } from "react-beautiful-dnd"; 

import "./board_style.css"

import TaskCard from "../TaksCard/taskcard";
import { useEffect } from 'react';

function Board(props) {

    const [ boardTaskCards, setBoardsTaskCards ] = useState([]);

    useEffect(() => {
        props.taskCards.map((task) => {
            if (task.status === props.boardName) {
                const newTaskCards = [...boardTaskCards];
                newTaskCards.push(task);
                setBoardsTaskCards(newTaskCards);
            }
        })
    }, [props.boardName, props.taskCards, boardTaskCards])

    return (
        <aside>
            <h3>{props.boardName}</h3>
            <section>
                    <Droppable droppableId='todo'>
                        {(provided) => (
                            <article
                                ref={provided.innerRef}
                                {...provided.droppableProps}
                            >
                                { boardTaskCards !== null && boardTaskCards !== undefined && Array.isArray(boardTaskCards) ? (
                                
                                    boardTaskCards.map((task, index) => {                  
                                        
                                        return <TaskCard 
                                            key={index}
                                            index={index}
                                            taskKey={task.key}
                                            taskName={task.taskname}
                                            taskDescription={task.taskdescription}
                                            taskPriority={task.taskpriority}
                                            setTriggerViewTask={props.setPopupTask}
                                            setTaskDataView={props.setTaskDataView}
                                        />
                                        
                                        })
                                    ) : ""
                                }

                                {provided.placeholder}
                                </article>
                            )
                        }
                    </Droppable>
            </section>
        </aside>

    )
}

export default Board;
