import './login_style.css';
import "../../styles/global.css"
import eye_icon from "../../img/eye-icon.png"

import React, { useState, useEffect } from "react"

function App() {

  // const [data, setData] = useState([{}])

  // useEffect(() => {
  //   fetch("/teste")
  //   .then(response => response.json())
  //   .then(data => {
  //     setData(data)
  //   })
  // }, [])
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append('user_credentials', [username, password]);

    fetch('http://localhost:5000/login', {
      method: 'POST',
      body: formData
    })
      .then(response => response.json())
      .then(data => console.log('Resposta do servidor:', data))
      .catch(error => console.error(error));
  };

  return (
    <body>
      <div className="login-container">
        <h1>Login</h1>

        <div className='userCredentials-inputs'>

          <input type='text' value={ username } id='username' className='username_input' placeholder='Insira o seu usuário'></input>

          <div className='password-container'>
            <input type='password' value={ password } id='password' className='password_input' placeholder='Insira sua senha'></input>
            <img className='eye-icon' src={ eye_icon } alt='eye-icon'/>
          </div>


          <button type='button' onClick={ handleSubmit } className='btn_singIn'>Entrar</button>

        </div>

        <div className='links'>
          <p>Não tem cadastro?</p>
          <a href='/'>Clique aqui</a>
        </div>

      </div>


        {/* {
          (typeof data.teste === 'undefined') ? (
            <p>Loading...</p>
            ) : (
              data.teste.map((teste, i) => (
                <p key={i}>{teste}</p>
                ))
                )
              } */}

    </body>
  );
}

export default App;
