import { React, useState } from "react";
import { RedirectFunction } from 'react-router-dom'

import "./register_style.css"
import "../../styles/global.css"
import eye_icon from "../../img/eye-icon.png"

import left_arrow from "../../img/left-arrow.png"

function Register() {

    const [ name, setName ] = useState("");
    const [ email, setEmail ] = useState("");
    const [ dateBirth, setDateBirth ] = useState("");
    const [ password, setPassword ] = useState("");
    const [ confirmedPassword, setConfirmedPassword ] = useState("");
    const [ isPasswordVisible, setIsPasswordVisible ] = useState("");
    const [ isConfirmedPasswordVisible, setIsConfirmedPasswordVisible ] = useState("");
    const [redirect, setRedirect] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (password === confirmedPassword) {
            
            const formData = new FormData();
            formData.append('user_singUp', [name, email, dateBirth, password]);
            
            fetch('http://localhost:5000/singUp', {
                method: 'POST',
                body: formData
            })
            .then(response => response.json())
            .then(data => {
                console.log('Resposta do servidor:', data)
            })
            .catch(err => console.error(err));
            
            setName("")
            setEmail("")
            setDateBirth("")
            setPassword("")
            setConfirmedPassword("")

             // Ativa o redirecionamento
            setRedirect(true);
            
        } else {
            
            alert("Senhas divergentes! \nFavor verificar e inseri-las novamente.")
            setPassword("")
            setConfirmedPassword("")
        }
    }

    const handleTogglePassword = () => {
        setIsPasswordVisible(!isPasswordVisible);
        };

    const handleToggleConfirmedPassword = () => {
        setIsConfirmedPasswordVisible(!isConfirmedPasswordVisible)
    }

    if (redirect) {
        return <RedirectFunction to="/" />;
    }

    return (
        <body>
            <div className="register-container">
                <a className="returnPage-link" href="/">
                    <img 
                        src={left_arrow} 
                        alt="page-back"
                        className="returnPage-img"
                    />
                </a>
                
                <h1 className="singUp-title">Fazer Cadastro</h1>

                <form className="singUp-form" onSubmit={handleSubmit}>
                    <div className="inputs">
                        <label className="singUp-labels" htmlfor="userFullName">Nome Completo: *</label>
                        <input 
                            type="text"
                            id="userFullName"
                            className="name_input"
                            onChange={(e) => setName(e.target.value)}
                            value={name}
                            required
                        />
                        <label className="singUp-labels" htmlfor="userEmail">E-mail: *</label>
                        <input 
                            type="email"
                            id="userEmail"
                            className="email_input"
                            onChange={(e) => setEmail(e.target.value)}
                            value={email}
                            required
                        />
                        <label className="singUp-labels" htmlfor="userDateBirth">Data de Nascimento:</label>
                        <input 
                            type="date"
                            id="userDateBirth"
                            className="dateBirth_input"
                            onChange={(e) => setDateBirth(e.target.value)}
                            value={dateBirth}
                        />
                        <label className="singUp-labels" htmlfor="userPassword">Senha: *</label>
                        
                        <div className='singUp-password-container'>
                            <input 
                                type={isPasswordVisible ? 'text' : 'password'}
                                id="userPassword"
                                className="passwordInput"
                                onChange={(e) => setPassword(e.target.value)}
                                value={password}
                                required
                            />
                            <img 
                                className='eye-icon' 
                                src={ eye_icon } 
                                alt='eye-icon'
                                onClick={handleTogglePassword}
                            />
                        </div>
                        
                        <label className="singUp-labels" htmlfor="confirmUserPassword">Confirmar Senha: *</label>
                        
                        <div className="singUp-password-container">
                            <input 
                                type={isConfirmedPasswordVisible ? 'text' : 'password'} 
                                id="confirmUserPassword"
                                className="confirmPasswordInput"
                                onChange={(e) => setConfirmedPassword(e.target.value)}
                                value={confirmedPassword}
                                required
                                />
                            <img 
                                className='eye-icon' 
                                src={ eye_icon } 
                                alt='eye-icon'
                                onClick={handleToggleConfirmedPassword}
                            />
                        </div>
                    </div>

                    <div className="container-btnSingUp">
                        <button 
                            type="submit"
                            className="bnt-singUp"
                            // ><Link to="/">Cadastrar</Link>
                            >Cadastrar
                        </button>
                    </div>
                </form>

            </div>
        </body>
    )
}

export default Register;
