import { useState } from "react";

import "./passwordInput_style.css"
import eye_icon from "../../img/eye-icon.png"

function PasswordInput({ labelName, htmlfor, state, onChange }) {

    const [ isPasswordVisible, setIsPasswordVisible ] = useState("");

    const handleTogglePassword = () => {
        setIsPasswordVisible(!isPasswordVisible);
    };

    return (

        <div className="container-component">

            <label className="passwordInput-label" htmlfor={htmlfor}>* {labelName}:</label>
        
            <div className='singUp-password-container'>

                <input 
                    type={isPasswordVisible ? 'text' : 'password'}
                    id="userPassword"
                    className="passwordInput"
                    onChange={(e) => onChange(e.target.value)}
                    value={state}
                    required
                    />
                <img 
                    className='eye-icon' 
                    src={ eye_icon } 
                    alt='eye-icon'
                    onClick={handleTogglePassword}
                    />
            </div>
        </div>
    )
}

export default PasswordInput;
