import React, { useState } from "react";
import eye_icon from "../../../img/eye-icon.png";
import "./passwordInput_style.css";

function PasswordInput({ id, state, onChange }) {
  const [isPasswordVisible, setIsPasswordVisible] = useState(false);

  const validatePassword = (password) => {
    // Expressão regular para garantir que a senha seja forte
    const strongPasswordRegex = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*])(?=.{8,})/;

    if (!password) {
      return "";
    } else if (!strongPasswordRegex.test(password)) {
      return "Senha fraca. Deve conter pelo menos 8 caracteres, incluindo letras maiúsculas, minúsculas, números e caracteres especiais.";
    }

    return ""; // Senha válida
  };

  const handleTogglePassword = () => {
    setIsPasswordVisible(!isPasswordVisible);
  };

  const passwordError = validatePassword(state);

  return (
    <div className="inputPassword-bodyContainer">

        <div className="inputPassword-container">

        <input
            type={isPasswordVisible ? "text" : "password"}
            id={id}
            className="passwordInput"
            onChange={(e) => onChange(e.target.value)}
            value={state}
            required
            />
        <img
            className="eye-icon"
            src={eye_icon}
            alt="eye-icon"
            onClick={handleTogglePassword}
            />
        </div>
      
      {passwordError && <p className="password-error">{passwordError}</p>}
    
    </div>
  );
}

export default PasswordInput;
