import { React, useState } from "react";

import "./register_style.css"
import "../../styles/global.css"

import left_arrow from "../../img/left-arrow.png"

function Register() {

    const [ name, setName ] = useState("");
    const [ email, setEmail ] = useState("");
    const [ dateBirth, setDateBirth ] = useState("");
    const [ password, setPassword ] = useState("");
    const [ confirmedPassword, setConfirmedPassword ] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (password === confirmedPassword) {
            
            const formData = new FormData();
            formData.append('user_singUp', [name, email, dateBirth, password]);
            
            fetch('http://localhost:5000/register', {
                method: 'POST',
                body: formData
            })
            .then(response => response.json())
            .then(data => {
                console.log('Resposta do servidor:', data)
            })
            .catch(err => console.error(err));
            
            setName("")
            setEmail("")
            setDateBirth("")
            setPassword("")
            
        } else {
            alert("Senhas divergentes. Favor verificar e inseri=las novamente")

            setPassword("")
            setConfirmedPassword("")
        }
    }

    return (
        <body>
            <div className="register-container">
                <a className="returnPage-link" href="/">
                    <img 
                        src={left_arrow} 
                        alt="page-back"
                        className="returnPage-img"
                    />
                </a>
                
                <h1 className="singUp-title">Fazer Cadastro</h1>

                <form className="container-inputs" onSubmit={handleSubmit}>
                    <div className="inputs" >
                        <label htmlfor="userFullName">Nome Completo: *</label>
                        <input 
                            type="text"
                            id="userFullName"
                            className="name_input"
                            onChange={(e) => setName(e.target.value)}
                            value={name}
                            required
                            />
                        <label htmlfor="userEmail">E-mail:</label>
                        <input 
                            type="email"
                            id="userEmail"
                            className="email_input"
                            onChange={(e) => setEmail(e.target.value)}
                            value={email}
                            />
                        <label htmlfor="userDateBirth">Data de Nascimento:</label>
                        <input 
                            type="date"
                            id="userDateBirth"
                            className="dateBirth_input"
                            onChange={(e) => setDateBirth(e.target.value)}
                            value={dateBirth}
                            />
                        <label htmlfor="userPassword">Senha: *</label>
                        <input 
                            type="password"
                            id="userPassword"
                            className="passwordInput"
                            onChange={(e) => setPassword(e.target.value)}
                            value={password}
                            required
                            />
                        <label htmlfor="confirmUserPassword">Confirmar Senha: *</label>
                        <input 
                            type="password"
                            id="confirmUserPassword"
                            className="confirmPasswordInput"
                            onChange={(e) => setConfirmedPassword(e.target.value)}
                            value={confirmedPassword}
                            required
                            />
                    </div>
                    <button 
                        type="submit"
                        className="bnt-singUp"
                        >Cadastrar
                    </button>
                </form>

            </div>
        </body>
    )
}

export default Register;
